def optimize_input(input)
  optimized = ''
  loop do
    optimized = trim_matching_pairs(input)
    break if optimized === input
    input = optimized
  end
  optimized
end

def optimize_input2(input)
  optimized = input
  loop do
    optimized.gsub!('()','')
    optimized.gsub!(')(','')
    break if optimized === input
    input = optimized
  end
  optimized
end

def trim_matching_pairs(input)
  input.gsub!('()','')
  input.gsub!(')(','')
  input
end

puts "\n1:"
time = Benchmark.realtime do
  floor = 0
  puts INPUT.split('').reduce(floor){ |level, char|
    case char
    when '('
      floor += 1
    when ')'
      floor -= 1
    end
  }
end
puts "Time elapsed #{time*1000} milliseconds"

